func TestDecode(t *testing.T) {
	cases := []struct {
		in   io.Reader
		want reducedPacket
	}{
		{bytes.NewReader(nrpePacket), reducedPacket{1, "check_load", nil}},
	}
	for _, c := range cases {
		got := decode(c.in)
		if got != c.want {
			t.Errorf("decode(nrpePacket) == %#v, want %#v", got, c.want)
		}
	}
}
