func TestDecode(t *testing.T) {
	cases := []struct {
		in   io.Reader
		want reducedPacket
	}{
		{bytes.NewReader(nrpePacket), reducedPacket{1, 0, "check_load"}},
		{bytes.NewReader(request), reducedPacket{1, 0, "check_users"}},
	}
	for _, c := range cases {
		got, err := decode(c.in)
		if got != c.want {
			t.Errorf("decode(nrpePacket) == %v, want %v", got, c.want)
		}
		if err != nil {
			t.Error(err)
		}
	}
}

func TestDecodecrc32(t *testing.T) {
	nrpePacketCopy := make([]byte, len(nrpePacket))
	copy(nrpePacketCopy, nrpePacket)
	nrpePacketCopy[6] = 0x2d
	cases := []struct {
		in   io.Reader
		want reducedPacket
	}{
		{bytes.NewReader(nrpePacketCopy), reducedPacket{1, 0, "check_load"}},
	}
	for _, c := range cases {
		got, err := decode(c.in)
		if got != c.want {
			t.Errorf("decode(nrpePacket) == %v, want %v", got, c.want)
		}
		if err != nil {
			t.Error(err)
		}
	}
}

func TestDecodeEncodeV3(t *testing.T) {
	cases := []struct {
		in   reducedPacket
		want reducedPacket
	}{
		{reducedPacket{1, 0, "connection successful"}, reducedPacket{2, 0, "connection successful"}},
	}
	for _, c := range cases {
		inter, _ := encodeV3(c.in)
		got, err := decode(bytes.NewReader(inter))
		if got != c.want {
			t.Errorf("decode(encodeV3(%v)) == %v, want %v", c.in, got, c.want)
		}
		if err != nil {
			t.Error(err)
		}
	}
}

func TestEncodeV3(t *testing.T) {
	cases := []struct {
		in   reducedPacket
		want []byte
	}{
		{reducedPacket{1, 1, "WARNING - load average: 0.02, 0.07, 0.06|load1=0.022;0.150;0.300;0; load5=0.068;0.100;0.250;0; load15=0.060;0.050;0.200;0; "}, packet},
		{reducedPacket{1, 0, "USERS OK - 1 users currently logged in |users=1;5;10;0"}, answer},
	}
	for _, c := range cases {
		got, err := encodeV3(c.in)
		if len(got) != len(c.want) {
			t.Errorf("encodeV3(%v) == %v, want %v", c.in, got, c.want)
			break
		}
		for i := 0; i < len(got); i++ {
			if got[i] != c.want[i] {
				t.Errorf("encodeV3(%v) == %v, want %v", c.in, got, c.want)
				break
			}
		}
		if err != nil {
			t.Error(err)
		}
	}
}
